import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Form, Button } from 'react-bootstrap';
import { Link, useSearchParams } from 'react-router-dom';
import axios from 'axios';
import { FaHeart, FaRegHeart } from 'react-icons/fa';

const Items = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchParams] = useSearchParams();
  const [filters, setFilters] = useState({
    category: searchParams.get('category') || '',
    minPrice: '',
    maxPrice: '',
    search: ''
  });
  const [likedItems, setLikedItems] = useState({});
  const [likesMap, setLikesMap] = useState({});

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/items');
        setItems(response.data);
        // 初始化 likesMap
        const map = {};
        response.data.forEach(item => { map[item._id] = item.likes || 0; });
        setLikesMap(map);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch items');
        setLoading(false);
      }
    };

    fetchItems();
  }, []);

  const filteredItems = items.filter(item => {
    if (filters.category && item.category !== filters.category) return false;
    if (filters.minPrice && item.price < parseFloat(filters.minPrice)) return false;
    if (filters.maxPrice && item.price > parseFloat(filters.maxPrice)) return false;
    if (filters.search && !item.title.toLowerCase().includes(filters.search.toLowerCase())) return false;
    return true;
  });

  const handleLike = async (itemId) => {
    if (likedItems[itemId]) return;
    try {
      const response = await axios.patch(`http://localhost:5000/api/items/${itemId}/like`);
      setLikesMap(prev => ({ ...prev, [itemId]: response.data.likes }));
      setLikedItems(prev => ({ ...prev, [itemId]: true }));
    } catch (err) {
      alert('Failed to like this item');
    }
  };

  if (loading) return <div className="container mt-5">Loading...</div>;
  if (error) return <div className="container mt-5 text-danger">{error}</div>;

  return (
    <Container className="my-5">
      <h1 className="mb-4">Browse Items</h1>
      
      {/* Filters */}
      <Card className="mb-4">
        <Card.Body>
          <Form>
            <Row>
              <Col md={3}>
                <Form.Group>
                  <Form.Label>Category</Form.Label>
                  <Form.Select
                    value={filters.category}
                    onChange={(e) => setFilters({...filters, category: e.target.value})}
                  >
                    <option value="">All Categories</option>
                    <option value="textbooks">Textbooks</option>
                    <option value="electronics">Electronics</option>
                    <option value="furniture">Furniture</option>
                    <option value="other">Other</option>
                  </Form.Select>
                </Form.Group>
              </Col>
              <Col md={3}>
                <Form.Group>
                  <Form.Label>Min Price</Form.Label>
                  <Form.Control
                    type="number"
                    value={filters.minPrice}
                    onChange={(e) => setFilters({...filters, minPrice: e.target.value})}
                    placeholder="Min"
                  />
                </Form.Group>
              </Col>
              <Col md={3}>
                <Form.Group>
                  <Form.Label>Max Price</Form.Label>
                  <Form.Control
                    type="number"
                    value={filters.maxPrice}
                    onChange={(e) => setFilters({...filters, maxPrice: e.target.value})}
                    placeholder="Max"
                  />
                </Form.Group>
              </Col>
              <Col md={3}>
                <Form.Group>
                  <Form.Label>Search</Form.Label>
                  <Form.Control
                    type="text"
                    value={filters.search}
                    onChange={(e) => setFilters({...filters, search: e.target.value})}
                    placeholder="Search items..."
                  />
                </Form.Group>
              </Col>
            </Row>
          </Form>
        </Card.Body>
      </Card>

      {/* Items Grid */}
      <Row>
        {filteredItems.map((item) => (
          <Col key={item._id} md={4} className="mb-4">
            <Card>
              <div style={{ position: 'absolute', top: 10, right: 16, zIndex: 2 }}>
                <Button
                  variant="link"
                  style={{ color: likedItems[item._id] ? 'red' : '#bbb', fontSize: 24, padding: 0 }}
                  onClick={() => handleLike(item._id)}
                  disabled={likedItems[item._id]}
                >
                  {likedItems[item._id] ? <FaHeart /> : <FaRegHeart />}
                </Button>
                <span style={{ fontSize: 14, color: '#888' }}>{likesMap[item._id] || 0}</span>
              </div>
              {item.images && item.images.length > 0 && (
                <Card.Img
                  variant="top"
                  src={`http://localhost:5000/uploads/${item.images[0].replace(/^uploads[\\/]/, '')}`}
                  alt={item.title}
                  style={{ height: '200px', objectFit: 'cover' }}
                />
              )}
              <Card.Body>
                <Card.Title>{item.title}</Card.Title>
                <Card.Text className="text-primary">
                  <strong>${item.price}</strong>
                </Card.Text>
                <Button
                  variant="primary"
                  as={Link}
                  to={`/items/${item._id}`}
                >
                  View Details
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default Items;
