import React, { useEffect, useState } from 'react';
import { Container, Card, Row, Col, Button } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

function Profile() {
    const { currentUser } = useAuth();
    const [listingsCount, setListingsCount] = useState(0);
    const [favoritesCount, setFavoritesCount] = useState(0);
    const [purchasesCount, setPurchasesCount] = useState(0);
    // 假设用户有 rating 字段，头像用 profileImage 或默认

    useEffect(() => {
        if (!currentUser) return;
        // 获取我的售卖数量
        axios.get('http://localhost:5000/api/items?status=all').then(res => {
            const myListings = res.data.filter(item => item.seller && (item.seller._id === currentUser.id || item.seller === currentUser.id));
            setListingsCount(myListings.length);
            // 获取我喜欢的商品数量
            const myFavorites = res.data.filter(item => Array.isArray(item.likedBy) && item.likedBy.includes(currentUser.id));
            setFavoritesCount(myFavorites.length);
            // 假设 status 为 Sold 且 buyer 字段为当前用户即为购买历史
            const myPurchases = res.data.filter(item => item.status === 'Sold' && item.buyer === currentUser.id);
            setPurchasesCount(myPurchases.length);
        });
    }, [currentUser]);

    if (!currentUser) {
        return (
            <Container className="mt-5">
                <h2>Please log in to view your profile</h2>
            </Container>
        );
    }

    return (
        <Container className="mt-5">
            <Row className="justify-content-center mb-4">
                <Col md={8} className="d-flex align-items-center">
                    <img
                        src={currentUser.profileImage ? `http://localhost:5000${currentUser.profileImage}` : 'https://randomuser.me/api/portraits/men/32.jpg'}
                        alt="avatar"
                        style={{ width: 100, height: 100, borderRadius: '50%', objectFit: 'cover', marginRight: 32 }}
                    />
                    <div>
                        <h2 style={{ marginBottom: 8 }}>{currentUser.name}</h2>
                        <div style={{ color: '#888', marginBottom: 4 }}>{currentUser.email}</div>
                        <div style={{ fontSize: 20, color: '#f7b500', display: 'flex', alignItems: 'center' }}>
                            <span style={{ fontSize: 22, marginRight: 6 }}>★</span>
                            {currentUser.rating ? `${currentUser.rating} / 5` : '4.5 / 5'}
                        </div>
                    </div>
                </Col>
            </Row>
            <Row className="justify-content-center">
                <Col md={5} sm={10} xs={12} className="mb-4 d-flex justify-content-center">
                    <Card className="text-center h-100 w-100" style={{ minHeight: 180, maxWidth: 400, boxShadow: '0 2px 12px #e0e7ef' }}>
                        <Card.Body>
                            <Card.Title style={{ fontSize: 28, marginBottom: 16 }}>My Listings</Card.Title>
                            <div style={{ fontSize: 24, color: '#2563eb', marginBottom: 16 }}>{listingsCount} active</div>
                            <Button variant="outline-primary" className="w-100" style={{ fontSize: 20, padding: '10px 0' }} href="/items?my=1">View</Button>
                        </Card.Body>
                    </Card>
                </Col>
                <Col md={5} sm={10} xs={12} className="mb-4 d-flex justify-content-center">
                    <Card className="text-center h-100 w-100" style={{ minHeight: 180, maxWidth: 400, boxShadow: '0 2px 12px #e0e7ef' }}>
                        <Card.Body>
                            <Card.Title style={{ fontSize: 28, marginBottom: 16 }}>Favorites</Card.Title>
                            <div style={{ fontSize: 24, color: '#e0245e', marginBottom: 16 }}>{favoritesCount} items</div>
                            <Button variant="outline-primary" className="w-100" style={{ fontSize: 20, padding: '10px 0' }} href="/items?fav=1">View</Button>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
            <Row className="justify-content-center">
                <Col md={5} sm={10} xs={12} className="mb-4 d-flex justify-content-center">
                    <Card className="text-center h-100 w-100" style={{ minHeight: 180, maxWidth: 400, boxShadow: '0 2px 12px #e0e7ef' }}>
                        <Card.Body>
                            <Card.Title style={{ fontSize: 28, marginBottom: 16 }}>Purchase History</Card.Title>
                            <div style={{ fontSize: 24, color: '#2563eb', marginBottom: 16 }}>{purchasesCount} items</div>
                            <Button variant="outline-primary" className="w-100" style={{ fontSize: 20, padding: '10px 0' }} href="/items?history=1">View</Button>
                        </Card.Body>
                    </Card>
                </Col>
                <Col md={5} sm={10} xs={12} className="mb-4 d-flex justify-content-center">
                    <Card className="text-center h-100 w-100" style={{ minHeight: 180, maxWidth: 400, boxShadow: '0 2px 12px #e0e7ef' }}>
                        <Card.Body>
                            <Card.Title style={{ fontSize: 28, marginBottom: 16 }}>Settings</Card.Title>
                            <div style={{ fontSize: 24, color: '#888', marginBottom: 16 }}>&nbsp;</div>
                            <Button variant="outline-secondary" className="w-100" style={{ fontSize: 20, padding: '10px 0' }} href="/settings">Edit</Button>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
}

export default Profile;
