import React, { useEffect, useState } from 'react';
import { Container, Card, Row, Col, Button, ListGroup, Badge } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Profile() {
    const { currentUser } = useAuth();
    const [listings, setListings] = useState([]);
    const [likedItems, setLikedItems] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const fetchUserData = async () => {
        if (!currentUser) return;
        try {
            // 获取用户的所有商品
            const listingsResponse = await axios.get(`http://localhost:5000/api/items?seller=${currentUser._id}`);
            setListings(listingsResponse.data);

            // 获取用户喜欢的商品
            const likedResponse = await axios.get(`http://localhost:5000/api/items?likedBy=${currentUser._id}`);
            setLikedItems(likedResponse.data);
        } catch (err) {
            setError('Failed to fetch user data');
            console.error('Error fetching user data:', err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchUserData();
    }, [currentUser]);

    const handleStatusChange = async (itemId, newStatus) => {
        try {
            await axios.patch(`http://localhost:5000/api/items/${itemId}/status`, {
                status: newStatus,
                buyerId: currentUser._id
            });
            // Refresh the items list
            fetchUserData();
        } catch (err) {
            setError('Failed to update status');
        }
    };

    const renderStatusButton = (item) => {
        switch (item.status) {
            case 'Available':
                return (
                    <Button
                        variant="warning"
                        size="sm"
                        onClick={() => handleStatusChange(item._id, 'Pending')}
                        className="me-2"
                    >
                        Mark as Pending
                    </Button>
                );
            case 'Pending':
                return (
                    <Button
                        variant="secondary"
                        size="sm"
                        onClick={() => handleStatusChange(item._id, 'Sold')}
                        className="me-2"
                    >
                        Mark as Sold
                    </Button>
                );
            case 'Sold':
                return (
                    <Button
                        variant="success"
                        size="sm"
                        disabled
                    >
                        Transaction Completed
                    </Button>
                );
            default:
                return null;
        }
    };

    if (!currentUser) {
        return (
            <Container className="mt-5">
                <h2>Please log in to view your profile</h2>
            </Container>
        );
    }

    if (loading) return <Container className="mt-5">Loading...</Container>;
    if (error) return <Container className="mt-5 text-danger">{error}</Container>;

    return (
        <Container className="mt-5">
            <Row className="justify-content-center mb-4">
                <Col md={8} className="d-flex align-items-center">
                    <img
                        src={currentUser.profileImage ? `http://localhost:5000${currentUser.profileImage}` : 'https://randomuser.me/api/portraits/men/32.jpg'}
                        alt="avatar"
                        style={{ width: 100, height: 100, borderRadius: '50%', objectFit: 'cover', marginRight: 32 }}
                    />
                    <div>
                        <h2 style={{ marginBottom: 8 }}>{currentUser.name}</h2>
                        <div style={{ color: '#888', marginBottom: 4 }}>{currentUser.email}</div>
                        <div style={{ fontSize: 20, color: '#f7b500', display: 'flex', alignItems: 'center' }}>
                            <span style={{ fontSize: 22, marginRight: 6 }}>★</span>
                            {currentUser.rating ? `${currentUser.rating} / 5` : '4.5 / 5'}
                        </div>
                    </div>
                </Col>
            </Row>

            <Row className="justify-content-center mb-4">
                <Col md={8}>
                    <Card>
                        <Card.Body>
                            <Card.Title>My Listings</Card.Title>
                            {listings.length === 0 ? (
                                <p>You haven't created any listings yet.</p>
                            ) : (
                                <ListGroup>
                                    {listings.map((item) => (
                                        <ListGroup.Item key={item._id} className="d-flex justify-content-between align-items-center">
                                            <div>
                                                <h5>{item.title}</h5>
                                                <p className="mb-0">${item.price}</p>
                                                <Badge bg={
                                                    item.status === 'Available' ? 'success' :
                                                    item.status === 'Pending' ? 'warning' : 'secondary'
                                                }>
                                                    {item.status}
                                                </Badge>
                                            </div>
                                            <div className="d-flex align-items-center">
                                                {renderStatusButton(item)}
                                                <Link to={`/items/${item._id}`} className="btn btn-primary">
                                                    View Details
                                                </Link>
                                            </div>
                                        </ListGroup.Item>
                                    ))}
                                </ListGroup>
                            )}
                        </Card.Body>
                    </Card>
                </Col>
            </Row>

            <Row className="justify-content-center mb-4">
                <Col md={8}>
                    <Card>
                        <Card.Body>
                            <Card.Title>Liked Items</Card.Title>
                            {likedItems.length === 0 ? (
                                <p>You haven't liked any items yet.</p>
                            ) : (
                                <ListGroup>
                                    {likedItems.map((item) => (
                                        <ListGroup.Item key={item._id} className="d-flex justify-content-between align-items-center">
                                            <div>
                                                <h5>{item.title}</h5>
                                                <p className="mb-0">${item.price}</p>
                                            </div>
                                            <Link to={`/items/${item._id}`} className="btn btn-primary">
                                                View Details
                                            </Link>
                                        </ListGroup.Item>
                                    ))}
                                </ListGroup>
                            )}
                        </Card.Body>
                    </Card>
                </Col>
            </Row>

            <Row className="justify-content-center">
                <Col md={8} className="text-center">
                    <Link to="/settings">
                        <Button variant="outline-secondary" size="lg">
                            Edit Profile
                        </Button>
                    </Link>
                </Col>
            </Row>
        </Container>
    );
}

export default Profile;
